using System.Collections;using UnityEngine;using UnityEngine.EventSystems;namespace iBicha{    public class EmojiTextureExample : MonoBehaviour, IPointerClickHandler    {        //Materials for the changing emoji texture        public Material material;        //EmojiTexture used for rendering        private EmojiTexture emojiTexture;        //Emoji list to shuffle from        private string[] emojis = { "☺", "❤", "🍆", "🍑" };        void Start()        {            emojiTexture = new EmojiTexture();            material.mainTexture = emojiTexture;            InvokeRepeating("ChangeEmoji", 0f, 1f);        }        //Change emoji every second        void ChangeEmoji()        {            emojiTexture.Text = emojis[Mathf.RoundToInt(Time.time) % emojis.Length];        }        //When the quad is tapped, we open the touch keyboard to input own emoji        public void OnPointerClick(PointerEventData eventData)        {            //Cancel shuffling of the emojis            CancelInvoke("ChangeEmoji");            //Read emoji from keyboard            TouchKeyboard.get.EditText(emojiTexture.Text, (success, text) =>            {                if (success)                    emojiTexture.Text = text;            });        }    }}